class LinkActivator{constructor(){this.observer=new IntersectionObserver(this.handleIntersect.bind(this),{threshold:.3}),this.init()}init(){document.querySelectorAll("a[data-target]").forEach((t=>{const e=t.getAttribute("data-target"),r=document.getElementById(e);r&&this.observer.observe(r)}))}handleIntersect(t){t.forEach((t=>{const e=t.target.id;document.querySelectorAll(`a[data-target="${e}"]`).forEach((e=>{t.isIntersecting?e.classList.add("active"):e.classList.remove("active")}))}))}}document.addEventListener("DOMContentLoaded",(()=>new LinkActivator));